var file = require('recordio-file');
var Task = require('../lib/task');

exports['task'] = {
  'test': function (test) {
    test.expect(1);

    var input = [{
      src: ['test/fixtures/foo.txt',
            'test/fixtures/bar.txt',
            'test/fixtures/baz.txt']
    }];
    var Test = Task.extend({
      map: function (config, source, dest) {
        return file.read(source);
      }
    });
    var task = new Test();
    task.run({}, input).then(function(result) {
      test.equal(true, true);
      test.done();
    });

  }
};
